import React, { useState, Suspense } from 'react';
import { IonIcon, IonGrid, IonRow, IonCol, IonItem } from '@ionic/react';
import ReactDOM from 'react-dom'
import { arrowBackCircleOutline, arrowForwardCircleOutline, play, } from 'ionicons/icons'
import {Howl, Howler} from 'howler';
import { FontAwesomeIcon } from '@fortawesome/react-fontawesome'
import { MediaService } from '../../services/MediaService'
import { faCoffee, faPlay, faBackward, faForward, faPause } from '@fortawesome/free-solid-svg-icons'
import './MediaPlayer.css'
import { GETURL } from '../../Constants';

interface MeadiaPlayerProps {}


export interface ProgressBarProps {
    progress:number
}

export interface ProgressBarState {

}

class ProgressBar extends React.Component<ProgressBarProps, ProgressBarState> {
    state = { 
        progress:0
    }

    setProgress = (pro:number) =>{
        this.state.progress = pro;
        this.setState(this.state);
    }
    render() { 
        return (
            <div id="progress-container">
                <div id="progress-bar" style={{width: this.props.progress + "%"}}></div>
            </div>
        );
    }
}


export interface MediaPlayerProps {

}

export interface MediaPlayerState {

}
interface Video{
    selected:boolean,
    ext:string,
    title:string,
    videoID:string
}
interface State{
    playing:boolean,
    videos:Video[],
    progress:number
}

class MediaPlayer extends React.Component<MediaPlayerProps, MediaPlayerState> {
    Sounds = new Howl({src: [""], format:["mp3"]});
    songSelected = false;
    state:State = {
        playing:false, 
        videos:[],
        progress:0
    };
    nextSong = ()=>{
        let i = 0;
        const len = this.state.videos.length;
        for(i = 0; i < len; i++){
            if(this.state.videos[i].selected){
                this.playSong(this.state.videos[(i+1)%len]);
                return;
            }
        }
    };
    previousSong =()=>{
        let i = 0;
        const len = this.state.videos.length;
        for(i = 0; i < len; i++){
            if(this.state.videos[i].selected){
                this.playSong(this.state.videos[(i-1)%len]);
            }
        }
    }
    setProgress = () =>{
        if(this.Sounds.state() == "loaded"){
            this.state.progress = Math.floor(Number(this.Sounds.seek())/this.Sounds.duration()*100);
        }

    };


    MusicList = async()=>{
        let videoInformation = await MediaService.getVideoInformation();
        let results:any[] = videoInformation;
        this.state.videos = results;
        this.setState({videos:results})
    };
    CreateHowl = (src:string)=>{
        return new Howl({
            src:src,
            onend: (id)=>{
                this.nextSong();
            }
        });
    };
    togglePlaying = () =>{
        if(!this.songSelected && this.state.videos.length > 0){
            this.songSelected = true;
            this.playSong(this.state.videos[0]);
        }
        else if(this.state.playing){
            this.Sounds.pause();
            this.state.playing = false;
        }
        else{
            this.Sounds.play();
            this.state.playing = true;
        }
        this.setState(this.state);
    };

    playSong = (video:Video)=>{
        this.state.videos.forEach((element,index) => {
            if(element.videoID == video.videoID){
                this.state.videos[index].selected = true;
            }
            else{
                this.state.videos[index].selected = false;
            }
        });
        this.songSelected = true;
        let videoID = video.videoID;
        let ext = video.ext;

        let videoUrl = `${GETURL}/youtube?v=${videoID}&f=${ext}`
        console.log(videoUrl);

        this.Sounds.unload();
        this.Sounds = new Howl({src: [videoUrl], format:ext, volume:.1});
        this.Sounds.play();
        this.state.playing = true;
        video.selected = true;
        this.setState({});
    };

    componentDidMount(){
        this.MusicList();
        setInterval(this.setProgress,1000);
    };

    render() { 
        const element = (
            <div>
                <IonGrid>

                    <IonRow>
                        <IonCol>
                            <div>
                                <Suspense fallback= {<div>Loading...</div>}>
                                    {this.state.videos.map(result =>{
                                        return <IonItem button className={`${true?"selected":""}`}  onClick={this.playSong.bind(this,result)}>{result.title}</IonItem>;
                                    })}
                                </Suspense>
                            </div>
                        </IonCol>
                    </IonRow>
                    <IonRow>
                        <IonCol>
                        </IonCol>
                        <IonCol size="8" sizeLg="4">
                            <ProgressBar progress={this.state.progress}></ProgressBar>
                        </IonCol>
                        <IonCol>
                        </IonCol>
                    </IonRow>
                    <IonRow>
                        <IonCol sizeLg="5">
                        </IonCol>
                        <IonCol size="1">
                            <FontAwesomeIcon size="2x" onClick={this.previousSong} icon={faBackward} />
                        </IonCol>
                        <IonCol size="1">
                            <FontAwesomeIcon className="pause-play" onClick={this.togglePlaying} size="2x" icon={this.state.playing? faPause:faPlay} />
                        </IonCol>
                        <IonCol size="1">
                            <FontAwesomeIcon size="2x" onClick={this.nextSong} icon={ faForward } />
                        </IonCol>
                        <IonCol sizeLg="5">
                        </IonCol>
                    </IonRow>
                </IonGrid>
            </div>
        );
        return element;
    }
}

export default MediaPlayer;
