import React from 'react';
import { IonIcon, IonGrid, IonRow, IonCol } from '@ionic/react';
import ReactDOM from 'react-dom'
import { arrowBackCircleOutline, arrowForwardCircleOutline, play, } from 'ionicons/icons'

import { FontAwesomeIcon } from '@fortawesome/react-fontawesome'
import { faCoffee, faPlay, faBackward, faForward, faPause } from '@fortawesome/free-solid-svg-icons'


interface MeadiaPlayerProps { 

}
let playing = true;



const MediaPlayer: React.FC<MeadiaPlayerProps> = () => {
    const togglePlaying = (elem : JSX.Element)=>{
        return ()=>{
            playing = !playing;
            ReactDOM.render(elem, document.getElementById('root'));
        }
    }

    const element = (
      <div>
          <IonGrid>
              <IonRow>
                  <IonCol>
                  </IonCol>
                  <IonCol size="1">
                      <FontAwesomeIcon size="2x" icon={faBackward} />
                  </IonCol>
                  <IonCol size="1">
                      <FontAwesomeIcon onClick={ togglePlaying(this) } size="2x" icon={playing? faPlay : faPause} />
                  </IonCol>
                  <IonCol size="1">
                      <FontAwesomeIcon size="2x" icon={ faForward} />
                  </IonCol>
                  <IonCol>
                  </IonCol>
              </IonRow>
          </IonGrid>
      </div>
    );

    return element;
};

const MediaController: React.FC<MeadiaPlayerProps> = () => {
    return (
        <div></div>
    );
};

export default MediaPlayer;
