import React, { useState } from 'react';
import { IonIcon, IonGrid, IonRow, IonCol } from '@ionic/react';
import ReactDOM from 'react-dom'
import { arrowBackCircleOutline, arrowForwardCircleOutline, play, } from 'ionicons/icons'

import { FontAwesomeIcon } from '@fortawesome/react-fontawesome'
import { faCoffee, faPlay, faBackward, faForward, faPause } from '@fortawesome/free-solid-svg-icons'
import './MediaPlayer.css'

interface MeadiaPlayerProps {}

const AudioController: React.FC<MeadiaPlayerProps> = React.forwardRef((props, ref) =>{
    let audioRef : React.RefObject<any> = React.createRef();
    return <audio ref="audioRef"></audio>

});

const ProgressBar: React.FC<MeadiaPlayerProps> = () => {
    return (
        <div id="progressBar"></div>
    );
};

const MediaPlayer: React.FC<MeadiaPlayerProps> = () => {
    const [playing, setPlaying] = useState(true);

    const togglePlaying = ()=>{
        setPlaying(!playing);
    }

    const element = (
        <div>
            <IonGrid>
                <IonRow>
                    <IonCol>
                    </IonCol>
                    <IonCol size="8" sizeLg="4">
                        <div id="progressContainer">
                            <ProgressBar></ProgressBar>
                        </div>
                    </IonCol>
                    <IonCol>
                    </IonCol>
                </IonRow>
                <IonRow>
                    <IonCol sizeLg="5">
                    </IonCol>
                    <IonCol size="1">
                        <FontAwesomeIcon size="2x" icon={faBackward} />
                    </IonCol>
                    <IonCol size="1">
                        <FontAwesomeIcon className="pausePlay" onClick={togglePlaying} size="2x" icon={playing? faPlay : faPause} />
                    </IonCol>
                    <IonCol size="1">
                        <FontAwesomeIcon size="2x" icon={ faForward } />
                    </IonCol>
                    <IonCol sizeLg="5">
                    </IonCol>
                </IonRow>
            </IonGrid>
        </div>
    );



    return element;

};



export default MediaPlayer;
